import { createActions, createReducer } from "reduxsauce";

export const { Types, Creators } = createActions({
  newUser: ["name", "birth_date"],
  inputTextName: ["name"],
  inputTextDate: ["birth_date"],
});

const INITIAL_STATE = {
  $id: 0,
  data: [],
  inputName: "",
  inputBirthDate: "",
};

const insert = (state = INITIAL_STATE, action) => ({
  ...state,
  $id: state.$id + 1,
  data: [
    {
      id: state.$id + 1,
      name: action.name,
      birth_date: action.birth_date,
    },
    ...state.data,
  ],
  inputName: "",
  inputBirthDate: "",
});

const change_name = (state = INITIAL_STATE, action) => ({
  ...state,
  inputName: action.inputName,
});

const change_birth_date = (state = INITIAL_STATE, action) => ({
  ...state,
  inputBirthDate: action.inputBirthDate,
});

export default createReducer(INITIAL_STATE, {
  [Types.NEW_USER]: insert,
  [Types.INPUT_TEXT_NAME]: change_name,
  [Types.INPUT_TEXT_DATE]: change_birth_date,
});
